import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button, Form, Modal } from 'react-bootstrap';
import api from '../services/api';

function Themes() {
  const [themes, setThemes] = useState([]);
  const [projects, setProjects] = useState([]);
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [show, setShow] = useState(false);
  const [name, setName] = useState('');
  const [selectedProjects, setSelectedProjects] = useState([]);
  const [selectedMembers, setSelectedMembers] = useState([]);
  const [themeHead, setThemeHead] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [themesRes, projectsRes, membersRes] = await Promise.all([
          api.get('/themes'),
          api.get('/projects?status=ongoing'), // incomplete projects
          api.get('/team')
        ]);
        setThemes(themesRes.data.data || themesRes.data);
        const projectsData = projectsRes.data.data || projectsRes.data;
        setProjects(Array.isArray(projectsData) ? projectsData : []);
        const membersData = membersRes.data.data || membersRes.data;
        setMembers(Array.isArray(membersData) ? membersData : []);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCreate = async () => {
    try {
      const data = {
        name,
        projects: selectedProjects,
        members: selectedMembers,
        themeHead: themeHead || null
      };

      const res = await api.post('/themes', data);
      const created = res.data.data || res.data;
      setThemes(prev => [created, ...prev]);
      setShow(false);
      resetForm();
    } catch (err) {
      console.error('Error creating theme:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Create failed');
    }
  };

  const resetForm = () => {
    setName('');
    setSelectedProjects([]);
    setSelectedMembers([]);
    setThemeHead('');
  };

  const handleProjectChange = (projectId) => {
    setSelectedProjects(prev =>
      prev.includes(projectId)
        ? prev.filter(id => id !== projectId)
        : [...prev, projectId]
    );
  };

  const handleMemberChange = (memberId) => {
    setSelectedMembers(prev =>
      prev.includes(memberId)
        ? prev.filter(id => id !== memberId)
        : [...prev, memberId]
    );
  };

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  return (
    <Container className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Themes</h1>
        <Button variant="primary" onClick={() => setShow(true)}>Add Theme</Button>
      </div>
      
      <Row>
        {themes.length === 0 ? (
          <Col>
            <p className="text-center text-muted">No themes found</p>
          </Col>
        ) : (
          themes.map(theme => (
            <Col md={6} lg={4} key={theme._id} className="mb-3">
              <Card>
                <Card.Body>
                  <Card.Title>{theme.name}</Card.Title>
                  <Card.Text>
                    <strong>Projects:</strong> {theme.projects?.map(p => p.name).join(', ') || 'None'}<br />
                    <strong>Members:</strong> {theme.members?.length || 0}<br />
                    <strong>Theme Head:</strong> {theme.themeHead?.name || 'Not assigned'}
                  </Card.Text>
                  <Button size="sm" variant="outline-primary">Edit</Button>{' '}
                  <Button size="sm" variant="outline-danger">Delete</Button>
                </Card.Body>
              </Card>
            </Col>
          ))
        )}
      </Row>

      {/* Create Modal */}
      <Modal show={show} onHide={() => setShow(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Add Theme</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Theme Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={e => setName(e.target.value)}
                placeholder="Enter theme name"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Select Projects (Incomplete)</Form.Label>
              {projects.map(project => (
                <Form.Check
                  key={project._id}
                  type="checkbox"
                  label={project.name}
                  checked={selectedProjects.includes(project._id)}
                  onChange={() => handleProjectChange(project._id)}
                />
              ))}
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Select Members</Form.Label>
              {members.map(member => (
                <Form.Check
                  key={member._id}
                  type="checkbox"
                  label={`${member.name} (${member.role})`}
                  checked={selectedMembers.includes(member._id)}
                  onChange={() => handleMemberChange(member._id)}
                />
              ))}
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Select Theme Head</Form.Label>
              <Form.Select value={themeHead} onChange={e => setThemeHead(e.target.value)}>
                <option value="">Select theme head</option>
                {members
                  .filter(member => ['Lead', 'Manager', 'Senior Developer'].includes(member.role))
                  .map(member => (
                    <option key={member._id} value={member._id}>
                      {member.name} ({member.role})
                    </option>
                  ))}
              </Form.Select>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShow(false)}>Cancel</Button>
          <Button variant="primary" onClick={handleCreate}>Create</Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default Themes;
