import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import api from '../services/api';

function Themes() {
  const [themes, setThemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [show, setShow] = useState(false);
  const [name, setName] = useState('');
  const [primaryColor, setPrimaryColor] = useState('#000000');
  const [secondaryColor, setSecondaryColor] = useState('#FFFFFF');

  useEffect(() => {
    const fetchThemes = async () => {
      try {
        const response = await api.get('/themes');
        setThemes(response.data.data || response.data);
      } catch (error) {
        console.error('Error fetching themes:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchThemes();
  }, []);

  const handleCreate = async () => {
    try {
      const res = await api.post('/themes', { name, primaryColor, secondaryColor });
      const created = res.data.data || res.data;
      setThemes(prev => [created, ...prev]);
      setShow(false);
      setName('');
      setPrimaryColor('#000000');
      setSecondaryColor('#FFFFFF');
    } catch (err) {
      console.error('Error creating theme:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Create failed');
    }
  };

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  return (
    <Container className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Themes</h1>
        <Button variant="primary" onClick={() => setShow(true)}>Add Theme</Button>
      </div>
      
      <Row>
        {themes.length === 0 ? (
          <Col>
            <p className="text-center text-muted">No themes found</p>
          </Col>
        ) : (
          themes.map(theme => (
            <Col md={6} lg={4} key={theme._id} className="mb-3">
              <Card>
                <Card.Body>
                  <Card.Title>{theme.name}</Card.Title>
                  <Card.Text>
                    <span style={{ 
                      display: 'inline-block', 
                      width: '20px', 
                      height: '20px', 
                      backgroundColor: theme.primaryColor,
                      marginRight: '10px'
                    }}></span>
                    Primary: {theme.primaryColor}
                  </Card.Text>
                  <Button variant="outline-primary">View Details</Button>
                </Card.Body>
              </Card>
            </Col>
          ))
        )}
      </Row>
    </Container>
  );
}

export default Themes;
