import React, { useEffect, useState } from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import api from '../services/api';

function Themes() {
  const [themes, setThemes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchThemes = async () => {
      try {
        const response = await api.get('/api/themes');
        setThemes(response.data);
      } catch (error) {
        console.error('Error fetching themes:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchThemes();
  }, []);

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  return (
    <Container className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Themes</h1>
        <Button variant="primary">Add Theme</Button>
      </div>
      
      <Row>
        {themes.length === 0 ? (
          <Col>
            <p className="text-center text-muted">No themes found</p>
          </Col>
        ) : (
          themes.map(theme => (
            <Col md={6} lg={4} key={theme._id} className="mb-3">
              <Card>
                <Card.Body>
                  <Card.Title>{theme.name}</Card.Title>
                  <Card.Text>
                    <span style={{ 
                      display: 'inline-block', 
                      width: '20px', 
                      height: '20px', 
                      backgroundColor: theme.primaryColor,
                      marginRight: '10px'
                    }}></span>
                    Primary: {theme.primaryColor}
                  </Card.Text>
                  <Button variant="outline-primary">View Details</Button>
                </Card.Body>
              </Card>
            </Col>
          ))
        )}
      </Row>
    </Container>
  );
}

export default Themes;
