import React, { useEffect, useState } from 'react';
import { Container, Button, Table } from 'react-bootstrap';
import api from '../services/api';

function TeamMembers() {
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [show, setShow] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('member');

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const response = await api.get('/team');
        setMembers(response.data.data || response.data);
      } catch (error) {
        console.error('Error fetching team members:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMembers();
  }, []);

  const handleCreate = async () => {
    try {
      const res = await api.post('/team', { name, email, role });
      const created = res.data.data || res.data;
      setMembers(prev => [created, ...prev]);
      setShow(false);
      setName('');
      setEmail('');
      setRole('member');
    } catch (err) {
      console.error('Error creating team member:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Create failed');
    }
  };

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  return (
    <Container className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Team Members</h1>
        <Button variant="primary">Add Member</Button>
      </div>
      
      {members.length === 0 ? (
        <p className="text-center text-muted">No team members found</p>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {members.map(member => (
              <tr key={member._id}>
                <td>{member.name}</td>
                <td>{member.email}</td>
                <td>{member.role || 'Member'}</td>
                <td>
                  <Button size="sm" variant="outline-primary">Edit</Button>{' '}
                  <Button size="sm" variant="outline-danger">Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </Container>
  );
}

export default TeamMembers;
