import React, { useEffect, useState } from 'react';
import { Container, Button, Table } from 'react-bootstrap';
import api from '../services/api';

function TeamMembers() {
  const [members, setMembers] = useState([]);
  const [themes, setThemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [show, setShow] = useState(false);
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [workDetail, setWorkDetail] = useState('');
  const [theme, setTheme] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [membersRes, themesRes] = await Promise.all([
          api.get('/team'),
          api.get('/themes')
        ]);
        setMembers(membersRes.data.data || membersRes.data);
        const themesData = themesRes.data.data || themesRes.data;
        setThemes(Array.isArray(themesData) ? themesData : []);
        if (themesData && themesData.length > 0) {
          setTheme(themesData[0]._id);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCreate = async () => {
    try {
      const res = await api.post('/team', { name, role, workDetail, theme });
      const created = res.data.data || res.data;
      setMembers(prev => [created, ...prev]);
      setShow(false);
      setName('');
      setRole('');
      setWorkDetail('');
      if (themes.length > 0) setTheme(themes[0]._id);
    } catch (err) {
      console.error('Error creating team member:', err.response?.data || err.message);
      alert(err.response?.data?.message || 'Create failed');
    }
  };

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  return (
    <Container className="mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Team Members</h1>
        <Button variant="primary" onClick={() => setShow(true)}>Add Member</Button>
      </div>
      
      {members.length === 0 ? (
        <p className="text-center text-muted">No team members found</p>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {members.map(member => (
              <tr key={member._id}>
                <td>{member.name}</td>
                <td>{member.email}</td>
                <td>{member.role || 'Member'}</td>
                <td>
                  <Button size="sm" variant="outline-primary">Edit</Button>{' '}
                  <Button size="sm" variant="outline-danger">Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
      {/* Create Modal */}
      {show && (
        <div className="modal show d-block" tabIndex="-1">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Add Team Member</h5>
                <button type="button" className="btn-close" onClick={() => setShow(false)}></button>
              </div>
              <div className="modal-body">
                <div className="mb-3">
                  <label className="form-label">Name</label>
                  <input className="form-control" value={name} onChange={e => setName(e.target.value)} />
                </div>
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input type="email" className="form-control" value={email} onChange={e => setEmail(e.target.value)} />
                </div>
                <div className="mb-3">
                  <label className="form-label">Role</label>
                  <select className="form-select" value={role} onChange={e => setRole(e.target.value)}>
                    <option value="member">Member</option>
                    <option value="lead">Lead</option>
                    <option value="manager">Manager</option>
                  </select>
                </div>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setShow(false)}>Cancel</button>
                <button className="btn btn-primary" onClick={handleCreate}>Create</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </Container>
  );
}

export default TeamMembers;
