import React from 'react';
import { Navbar, Nav, Container, Spinner } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useAuth } from '../context/AuthContext';

const ADMIN_URL = process.env.REACT_APP_ADMIN_URL || 'http://localhost:3001';

const Navigation = () => {
  const { user, loading, logout } = useAuth();

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand>Project Management</Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <LinkContainer to="/">
              <Nav.Link>Home</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/projects">
              <Nav.Link>Projects</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/team">
              <Nav.Link>Team Members</Nav.Link>
            </LinkContainer>
            {loading ? (
              <Nav.Link disabled><Spinner animation="border" size="sm" /></Nav.Link>
            ) : !user ? (
              <>
                <LinkContainer to="/login">
                  <Nav.Link>Login</Nav.Link>
                </LinkContainer>
                <LinkContainer to="/signup">
                  <Nav.Link>Sign Up</Nav.Link>
                </LinkContainer>
              </>
            ) : (
              <>
                <LinkContainer to="/profile">
                  <Nav.Link>Profile</Nav.Link>
                </LinkContainer>
                {user.role === 'admin' && (
                  <Nav.Link href={ADMIN_URL} target="_blank" rel="noopener noreferrer">
                    Admin Panel
                  </Nav.Link>
                )}
                <Nav.Link onClick={logout} style={{ cursor: 'pointer' }}>
                  Logout
                </Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Navigation;
