import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Spinner, Table, Badge } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

const ThemeDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [theme, setTheme] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchThemeDetails();
  }, [id]);

  const fetchThemeDetails = async () => {
    try {
      setLoading(true);
      const response = await api.get(`/themes/${id}`);
      setTheme(response.data.data);
    } catch (error) {
      console.error('Error fetching theme details:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Container className="loading-spinner">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </Container>
    );
  }

  if (!theme) {
    return (
      <Container className="my-5">
        <Card>
          <Card.Body className="text-center">
            <p>Theme not found.</p>
            <button className="btn btn-primary" onClick={() => navigate('/projects')}>
              Back to Projects
            </button>
          </Card.Body>
        </Card>
      </Container>
    );
  }

  return (
    <Container className="my-5">
      <Row className="mb-4">
        <Col>
          <button 
            className="btn btn-outline-secondary mb-3" 
            onClick={() => navigate('/projects')}
          >
            ‚Üê Back to Projects
          </button>
          <Card>
            <Card.Body>
              <h1>{theme.name}</h1>
              <div className="mt-3">
                <div className="mb-3">
                  <strong>Active Projects:</strong>
                  {theme.projects && theme.projects.length > 0 ? (
                    theme.projects.map(project => (
                      <Badge key={project._id} bg={project.status === 'completed' ? 'success' : 'warning'} className="ms-2">
                        {project.name} ({project.status})
                      </Badge>
                    ))
                  ) : (
                    <span className="text-muted"> No projects assigned</span>
                  )}
                </div>
                <div className="mb-3">
                  <strong>Theme Head:</strong>
                  {theme.themeHead ? (
                    <Badge bg="primary" className="ms-2">
                      {theme.themeHead.name} ({theme.themeHead.role})
                    </Badge>
                  ) : (
                    <span className="text-muted"> Not assigned</span>
                  )}
                </div>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row>
        <Col>
          <h2 className="mb-4">Team Members ({theme.members ? theme.members.length : 0})</h2>
          {!theme.members || theme.members.length === 0 ? (
            <Card>
              <Card.Body className="text-center">
                <p className="text-muted">No team members assigned to this theme yet.</p>
              </Card.Body>
            </Card>
          ) : (
            <Table striped bordered hover responsive>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Role</th>
                  <th>Work Details</th>
                </tr>
              </thead>
              <tbody>
                {theme.members.map((member) => (
                  <tr key={member._id}>
                    <td><strong>{member.name}</strong></td>
                    <td>{member.role}</td>
                    <td>{member.workDetail}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default ThemeDetails;
