import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const apiClient = axios.create({
  baseURL: API_URL,
});

// Token add करने के लिए
apiClient.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Projects
export const getProjects = (status) => apiClient.get(`/projects${status ? `?status=${status}` : ''}`);
export const getProject = (id) => apiClient.get(`/projects/${id}`);
export const getProjectWithThemes = (id) => apiClient.get(`/projects/${id}/themes`);

// Themes
export const getThemes = (projectId) => apiClient.get(`/themes${projectId ? `?project=${projectId}` : ''}`);
export const getTheme = (id) => apiClient.get(`/themes/${id}`);
export const getThemeWithTeam = (id) => apiClient.get(`/themes/${id}/team`);

// Team Members
export const getTeamMembers = (themeId) => apiClient.get(`/team${themeId ? `?theme=${themeId}` : ''}`);
export const getTeamMember = (id) => apiClient.get(`/team/${id}`);

export default apiClient;
